mapper.select:
select id,card,name, phone,status,card_type cardType,is_admin isAdmin,sex,create_by createBy,
create_time createTime,remark
from sys_members
where card in (
    select card from
    sys_members


            AND card = #{card}


            AND name = #{name}


            AND phone= #{phone}


            AND status = #{status}


            and date_format(create_time,'%y%m%d %H%i%s') >= date_format(#{beginTime},'%y%m%d %H%i%s')


            and date_format(create_time,'%y%m%d %H%i%s') <= date_format(#{endTime},'%y%m%d %H%i%s')


)
or parent_card in (
    select card from
    sys_members


            AND card = #{card}


            AND name = #{name}


            AND phone= #{phone}


            AND status = #{status}


            and date_format(create_time,'%y%m%d %H%i%s') >= date_format(#{beginTime},'%y%m%d %H%i%s')


            and date_format(create_time,'%y%m%d %H%i%s') <= date_format(#{endTime},'%y%m%d %H%i%s')


    )

select sum(IF(card_type=0,1,0)) parentCount,
        sum(IF(card_type=1,1,0)) childCount,
        sum(deposit) depositCount,
        sum(repair) repairCount
from sys_members
where card in (
    select card from
    sys_members


            AND card = #{card}


            AND name = #{name}


            AND phone= #{phone}


            AND status = #{status}


            and date_format(create_time,'%y%m%d %H%i%s') >= date_format(#{beginTime},'%y%m%d %H%i%s')


            and date_format(create_time,'%y%m%d %H%i%s') <= date_format(#{endTime},'%y%m%d %H%i%s')


)
or parent_card in (
    select card from
    sys_members


            AND card = #{card}


            AND name = #{name}


            AND phone= #{phone}


            AND status = #{status}


            and date_format(create_time,'%y%m%d %H%i%s') >= date_format(#{beginTime},'%y%m%d %H%i%s')


            and date_format(create_time,'%y%m%d %H%i%s') <= date_format(#{endTime},'%y%m%d %H%i%s')


    )

mapper.update:
update sys_members
parent_card = #{parentCard}, card = #{card}, name = #{name}, phone = #{phone}, status = #{status}, card_type = #{cardType}, is_admin = #{isAdmin}, sex = #{sex}, remark = #{remark}, deposit = #{deposit}, repair = #{repair}, share_ratio = #{shareRatio}, rebate_ratio = #{rebateRatio}, is_pump = #{isPump}, is_cash = #{isCash}, is_settlement = #{isSettlement}, is_out = #{isOut}, baccarat_rolling_ratio_chip = #{baccaratRollingRatioChip}, baccarat_rolling_ratio_cash = #{baccaratRollingRatioCash}, dragon_tiger_ratio_chip = #{dragonTigerRatioChip}, dragon_tiger_ratio_cash = #{dragonTigerRatioCash}, update_time = sysdate(), update_by = #{updateBy}
where id = #{id}

mapper.delete:
delete from sys_members
where id in

    #{id}

mapper.update:
update sys_members


        chip =chip + #{chipAmount}


        chip =chip - #{chipAmount}


where card = #{card}

mapper.select:
SELECT IFNULL(SUM(m.chip),0) AS chipAmount FROM sys_members m
AND m.is_admin = 0
AND m.card = #{card}

SELECT m.card as card, m.`name` AS NAME, m.is_settlement AS isSettlement, IFNULL( w.water, 0 ) AS water, IFNULL( w.water_amount, 0 ) AS waterAmount, m.remark as remark, IFNULL( r.signed_amount , 0 )as signedAmount, m.`status` as status FROM sys_members m LEFT JOIN sys_members_water w ON w.card = m.card LEFT JOIN sys_signed_record r on r.card= m.card
AND m.is_admin = 0
AND m.card = #{card}
or m.parent_card = #{card}
ORDER BY w.water desc

SELECT IFNULL(SUM(w.water),0) AS water, IFNULL(SUM(w.water_amount),0) AS waterAmount FROM sys_members m LEFT JOIN sys_members_water w ON w.card = m.card
AND m.is_admin = 0
AND m.card = #{card}
or m.parent_card = #{card}
