mapper.update:
update sys_user
set password = #{password}
where user_name = #{userName}

update sys_user
user_name=#{userName}, nick_name=#{userName}, phonenumber=#{phonenumber}, sex=#{sex}, password=#{password}, address=#{address}, post=#{post}, join_time=#{joinTime}, brithday=#{brithday}, role_id=#{roleId}, remark=#{remark}, update_time = sysdate(), update_by = #{updateBy}
where user_id = #{userId}
