mapper.insert:
insert into sys_bet (card, table_id, version, boot_num, game_num, type, game_id, game_result, create_by, create_time)
values (#{card}, #{tableId}, #{version}, #{bootNum}, #{gameNum}, #{type}, #{gameId}, #{gameResult}, #{createBy},


            #{createTime}


            sysdate()


)

mapper.select:
SELECT bt.bet_id as betId, bt.card, bt.version, bt.table_id AS tableId, bt.boot_num AS bootNum, bt.game_num AS gameNum, bt.type, SUM(b.bet_money) as betMoney, bt.game_result AS gameResult, SUM(b.win_lose) as winLose, bt.create_time AS createTime, bt.create_by AS createBy FROM sys_bet bt LEFT JOIN sys_bet_info b on b.bet_id=bt.bet_id
AND bt.card = #{card}
AND bt.table_id = #{tableId}
AND bt.type = 0
AND bt.type = 1
AND bt.type = 2
AND bt.type = 3
AND bt.type in (0,1)
AND bt.type in (2,3)
AND bt.boot_num = #{bootNum}
AND bt.game_num = #{gameNum}
AND bt.create_by = #{createBy}
AND bt.create_time >= #{startTime}
AND bt.create_time <= #{endTime}
GROUP BY bt.bet_id ORDER BY bt.create_time,bt.card DESC

SELECT b.bet_id as betId, b.bet_option as betOption, b.bet_money as betMoney FROM sys_bet_info b
b.bet_id in
#{betId}

SELECT
    SUM( b.bet_money ) AS betMoney,
    SUM( b.win_lose ) AS winLose
FROM
sys_bet bt
LEFT JOIN sys_bet_info b on b.bet_id=bt.bet_id


        AND bt.card = #{card}



        AND bt.table_id = #{tableId}



        AND bt.game_id = #{gameId}



        AND bt.type = #{type}



        AND bt.boot_num = #{bootNum}


        AND bt.game_num = #{gameNum}


        AND bt.create_by = #{createBy}


        AND bt.create_time >= #{startTime}


        AND bt.create_time <= #{endTime}

SELECT m.card as card, m.`name` as name, date_format(b.create_time, '%Y-%m-%d') as date, ifnull(SUM(case when type in (0,1,4) then b.win_lose else 0 end), 0) as winLose, (select ifnull(SUM(case when type in (0,1,4) then a.win_lose else 0 end), 0) from sys_bet_info a WHERE date_format(a.create_time, '%Y-%m-%d') <= date_format(b.create_time, '%Y-%m-%d') and a.card = b.card) as sumWinLose, ifnull(SUM(case when type in(0,1,4) then b.water else 0 end), 0) as water, (select ifnull(SUM(case when type in(0,1,4) then a.water else 0 end), 0) from sys_bet_info a WHERE date_format(a.create_time, '%Y-%m-%d') <= date_format(b.create_time, '%Y-%m-%d') and a.card = b.card) as sumWater,
ifnull(SUM(case when type in (2,3,5) then b.win_lose else 0 end), 0) as winLoseTh, (select ifnull(SUM(case when type in (2,3,5) then a.win_lose else 0 end), 0) from sys_bet_info a WHERE date_format(a.create_time, '%Y-%m-%d') <= date_format(b.create_time, '%Y-%m-%d') and a.card = b.card) as sumWinLoseTh, ifnull(SUM(case when type in (2,3,5) then b.water else 0 end), 0) as waterTh, (select ifnull(SUM(case when type in (2,3,5) then a.water else 0 end), 0) from sys_bet_info a WHERE date_format(a.create_time, '%Y-%m-%d') <= date_format(b.create_time, '%Y-%m-%d') and a.card = b.card) as sumWaterTh FROM sys_bet_info b LEFT JOIN sys_members m ON m.card = b.card
AND b.card = #{card}
AND date_format(b.create_time,'%Y-%m-%d') >= #{startTime}
AND date_format(b.create_time,'%Y-%m-%d') <= #{endTime}
GROUP BY m.card,date_format(b.create_time,'%Y-%m-%d') ORDER BY date_format(b.create_time,'%Y-%m-%d') desc

SELECT
    SUM( b.win_lose ) AS winLose,
    SUM( b.water ) AS water
FROM
    sys_bet_info b


        AND b.card = #{card}


        AND date_format(b.create_time,'%Y-%m-%d') >= #{startTime}


        AND date_format(b.create_time,'%Y-%m-%d') <= #{endTime}

select m.card, m.name, ifnull(sum(case when type in (0, 1) then b.bet_money else 0 end), 0) as betMoney, ifnull(sum(case when type in (0, 1) then b.water else 0 end), 0) as water, ifnull(sum(case when type in (0, 1) then b.water_amount else 0 end), 0) as waterAmount, ifnull(sum(case when type in (0, 1) then b.tie else 0 end), 0) as tie, ifnull(sum(case when type in (0, 1) then b.pump when 1 then b.pump else 0 end), 0) as pump, ifnull(sum(case when type in (0, 1) then b.win_lose else 0 end), 0) as winLose,
ifnull(sum(case when type in (2, 3) then b.bet_money else 0 end), 0) as betMoneyTh, ifnull(sum(case when type in (2, 3) then b.water else 0 end), 0) as waterTh, ifnull(sum(case when type in (2, 3) then b.water_amount else 0 end), 0) as waterAmountTh, ifnull(sum(case when type in (2, 3) then b.tie else 0 end), 0) as tieTh, ifnull(sum(case when type in (2, 3) then b.pump else 0 end), 0) as pumpTh, ifnull(sum(case when type in (2, 3) then b.win_lose else 0 end), 0) as winLoseTh from sys_members m left join sys_bet_info b on b.card = m.card and b.type in (0, 1, 2, 3)
AND m.card = #{card}
AND (m.card = #{card} or m.parent_card = #{card})
AND m.is_admin = 0
AND b.table_id = #{tableId}
AND b.game_id = #{gameId}
AND b.type = #{operationType}
AND b.create_time >= #{startTime}
AND b.create_time <= #{endTime}
GROUP BY m.card order by ifnull(sum(case type when 0 then b.bet_money when 1 then b.bet_money else 0 end), 0) desc, ifnull(sum(case type when 2 then b.bet_money when 3 then b.bet_money else 0 end), 0) desc;

SELECT t.table_id as tableId, IFNULL(-SUM(CASE WHEN b.type = 0 THEN b.win_lose else 0 end), 0) as chipWinLose, IFNULL(-SUM(CASE WHEN b.type = 1 THEN b.win_lose else 0 end), 0) as cashWinLose, IFNULL(-SUM(case when type in (0, 1) then b.win_lose else 0 end), 0) as winLose, IFNULL(SUM(case when type in (0, 1) then b.water else 0 end), 0) as water,
IFNULL(-SUM(CASE WHEN b.type = 2 THEN b.win_lose else 0 end), 0) as chipWinLoseTh, IFNULL(-SUM(CASE WHEN b.type = 3 THEN b.win_lose else 0 end), 0) as cashWinLoseTh, IFNULL(-SUM(case when type in (2, 3) then b.win_lose else 0 end), 0) as winLoseTh, IFNULL(SUM(case when type in (2, 3) then b.water else 0 end), 0) as waterTh FROM sys_table_management t LEFT JOIN sys_bet_info b ON b.table_id = t.table_id /*and b.type !=2*/
AND t.is_delete=0
AND b.create_time >= #{startTime}
AND b.create_time <= #{endTime}
GROUP BY t.table_id ORDER BY t.table_id asc

SELECT t.table_id tid, IFNULL(-SUM(CASE WHEN b.type = 0 THEN b.win_lose else 0 end), 0) as chipWinLose, IFNULL(-SUM(CASE WHEN b.type = 1 THEN b.win_lose else 0 end), 0) as cashWinLose, IFNULL(-SUM(case when type in (0, 1) then b.win_lose else 0 end), 0) as winLose, IFNULL(SUM(case when type in (0, 1) then b.water else 0 end), 0) as water,
IFNULL(-SUM(CASE WHEN b.type = 2 THEN b.win_lose else 0 end), 0) as chipWinLoseTh, IFNULL(-SUM(CASE WHEN b.type = 3 THEN b.win_lose else 0 end), 0) as cashWinLoseTh, IFNULL(-SUM(case when type in (2, 3) then b.win_lose else 0 end), 0) as winLoseTh, IFNULL(SUM(case when type in (2, 3) then b.water else 0 end), 0) as waterTh FROM sys_table_management t LEFT JOIN sys_bet_info b ON b.table_id = t.table_id /*and b.type !=2*/
AND t.is_delete=0
AND b.create_time >= #{startTime}
AND b.create_time <= #{endTime}
