mapper.select:
SELECT id, table_id tableId, boot_num bootNum, sys_chip sysChip, person_chip personChip, chip_gap chipGap, chip_add chipAdd, sys_insurance sysInsurance, person_insurance personInsurance, insurance_gap insuranceGap, insurance_add insuranceAdd, water water, chip_win chipWin, insurance_win insuranceWin, create_time createTime, create_by createBy, remark FROM sys_porint
AND table_id = #{tableId}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
ORDER BY create_time desc

SELECT
if(sum(chip_add) is null ,0 ,sum(chip_add) ) chipAddCount,
if(sum(water) is null ,0 ,sum(water)) waterCount,
if(sum(chip_win) is null ,0 , sum(chip_win))chipWinCount,
if(sum(insurance_win) is null ,0 ,sum(insurance_win)) insuranceWinCount,
if(sum(insurance_add) is null ,0 , sum(insurance_add) )insuranceAddCount
FROM
sys_porint


        AND table_id = #{tableId}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}
