mapper.select:
SELECT t.game_id gameId, s.id, s.table_id tableId, s.boot_num bootNum, s.sys_chip+sys_cash sysChip, s.person_chip+person_cash personChip, s.chip_gap chipGap, s.chip_add chipAdd, s.cash_gap cashGap, s.cash_add cashAdd, s.sys_insurance sysInsurance, s.person_insurance personInsurance, s.insurance_gap insuranceGap, s.insurance_add insuranceAdd, s.water water, s.chip_win chipWin, s.insurance_win insuranceWin, s.create_time createTime, s.create_by createBy, s.remark, s.sys_chip_th+sys_cash_th sysChipTh, s.person_chip_th+person_cash_th personChipTh, s.chip_gap_th chipGapTh, s.chip_add_th chipAddTh, s.sys_cash_th sysCashTh, s.person_cash_th personCashTh, s.cash_gap_th cashGapTh, s.cash_add_th cashAddTh, s.sys_insurance_th sysInsuranceTh, s.person_insurance_th personInsuranceTh, s.insurance_gap_th insuranceGapTh, s.insurance_add_th insuranceAddTh, s.water_th waterTh, s.chip_win_th chipWinTh, s.insurance_win_th insuranceWinTh FROM sys_porint s left join sys_table_management t on s.table_id = t.table_id
AND s.table_id = #{tableId}
AND s.create_time >= #{startTime}
AND s.create_time <= #{endTime}
ORDER BY s.create_time desc

SELECT
if(sum(chip_add) is null ,0 ,sum(chip_add) ) chipAddCount,
if(sum(water) is null ,0 ,sum(water)) waterCount,
if(sum(chip_win) is null ,0 , sum(chip_win))chipWinCount,
if(sum(insurance_win) is null ,0 ,sum(insurance_win)) insuranceWinCount,
if(sum(insurance_add) is null ,0 , sum(insurance_add) )insuranceAddCount,

if(sum(chip_add_th) is null ,0 ,sum(chip_add_th) ) chipAddCountTh,
if(sum(water_th) is null ,0 ,sum(water_th)) waterCountTh,
if(sum(chip_win_th) is null ,0 , sum(chip_win_th))chipWinCountTh,
if(sum(insurance_win_th) is null ,0 ,sum(insurance_win_th)) insuranceWinCountTh,
if(sum(insurance_add_th) is null ,0 , sum(insurance_add_th) )insuranceAddCountTh
FROM
sys_porint


        AND table_id = #{tableId}


        AND create_time >= #{startTime}


        AND create_time <= #{endTime}

SELECT id, table_id tableId, boot_num bootNum, sys_chip sysChip, person_chip personChip, chip_gap chipGap, chip_add chipAdd, sys_insurance sysInsurance, person_insurance personInsurance, insurance_gap insuranceGap, insurance_add insuranceAdd, water water, chip_win chipWin, insurance_win insuranceWin, sys_chip_th sysChipTh, person_chip_th personChipTh, chip_gap_th chipGapTh, chip_add_th chipAddTh, sys_insurance_th sysInsuranceTh, person_insurance_th personInsuranceTh, insurance_gap_th insuranceGapTh, insurance_add_th insuranceAddTh, water_th waterTh, chip_win_th chipWinTh, insurance_win_th insuranceWinTh, create_time createTime, create_by createBy, remark FROM sys_porint_update
AND table_id = #{tableId}
AND boot_num = #{bootNum}
AND create_time >= #{startTime}
AND create_time <= #{endTime}
ORDER BY create_time desc
