mapper.select:
SELECT r.table_id AS tableId, r.chip AS chip, r.cash AS cash, r.insurance AS insurance, r.chip_add AS chipAdd, r.cash_add AS cashAdd, r.insurance_add AS insuranceAdd, r.water AS water, r.win AS win, r.insurance_win AS insuranceWin, r.create_time AS createTime, r.remark AS remark, r.chip_th AS chipTh, r.cash_th AS cashTh, r.insurance_th AS insuranceTh, r.chip_add_th AS chipAddTh, r.cash_add_th AS cashAddTh, r.insurance_add_th AS insuranceAddTh, r.water_th AS waterTh, r.win_th AS winTh, r.insurance_win_th AS insuranceWinTh FROM sys_receipt r
AND r.table_id = #{tableId}
AND date_format( r.create_time ,'%Y-%m-%d')>= #{startTime}
AND date_format( r.create_time ,'%Y-%m-%d')<= #{endTime}
ORDER BY r.create_time DESC

select MIN(s.version) minVersion ,MAX(s.version) maxVersion , s.table_id tableId
from sys_receipt s


        AND s.create_time >= #{startTime}


        AND s.create_time <= #{endTime}


group by s.table_id

select m.card, m.name, ifnull(sum(case when type in (0, 1,4) then b.bet_money else 0 end), 0) as betMoney, ifnull(sum(case when type in (0, 1,4) then b.water else 0 end), 0) as water, ifnull(sum(case when type in (0, 1,4) then b.water_amount else 0 end), 0) as waterAmount, ifnull(sum(case when type in (0, 1,4) then b.tie else 0 end), 0) as tie, ifnull(sum(case when type in (0, 1,4)then b.pump else 0 end), 0) as pump, ifnull(sum(case when type in (0, 1,4) then b.win_lose else 0 end), 0) as winLose,
ifnull(sum(case when type in (2, 3,5) then b.bet_money else 0 end), 0) as betMoneyTh, ifnull(sum(case when type in (2, 3,5) then b.water else 0 end), 0) as waterTh, ifnull(sum(case when type in (2, 3,5) then b.water_amount else 0 end), 0) as waterAmountTh, ifnull(sum(case when type in (2, 3,5) then b.tie else 0 end), 0) as tieTh, ifnull(sum(case when type in (2, 3,5) then b.pump else 0 end), 0) as pumpTh, ifnull(sum(case when type in (2, 3,5) then b.win_lose else 0 end), 0) as winLoseTh from sys_members m left join sys_bet_info b on b.card = m.card
AND b.table_id = #{reportSearch.tableId}
AND b.boot_num = #{reportSearch.bootNum}
AND b.game_id = #{reportSearch.gameId}
AND (
( b.version >= #{item.minVersion} AND b.version <= #{item.maxVersion} AND table_id = #{item.tableId} )
)
AND b.version = -1
AND m.card = #{reportSearch.card}
AND (m.card = #{reportSearch.card} or m.parent_card = #{reportSearch.card})
AND m.is_admin = 0
GROUP BY m.card ,m.NAME order by ifnull(sum(case type when 0 then b.bet_money when 1 then b.bet_money else 0 end), 0) desc, ifnull(sum(case type when 2 then b.bet_money when 3 then b.bet_money else 0 end), 0) desc

select
ifnull(sum(case when type in (0, 1,4) then b.bet_money else 0 end), 0)               as betMoney,
ifnull(sum(case when type in (0, 1,4) then b.water else 0 end), 0)                   as water,
ifnull(sum(case when type in (0, 1,4) then b.water_amount else 0 end), 0)            as waterAmount,
ifnull(sum(case when type in (0, 1,4) then b.tie else 0 end), 0)                     as tie,
ifnull(sum(case when type in (0, 1,4)then b.pump else 0 end), 0)  as pump,
ifnull(sum(case when type in (0, 1,4) then b.win_lose else 0 end), 0)                as winLose,

ifnull(sum(case when type in (2, 3,5) then b.bet_money else 0 end), 0)               as betMoneyTh,
ifnull(sum(case when type in (2, 3,5) then b.water else 0 end), 0)                   as waterTh,
ifnull(sum(case when type in (2, 3,5) then b.water_amount else 0 end), 0)            as waterAmountTh,
ifnull(sum(case when type in (2, 3,5) then b.tie else 0 end), 0)                     as tieTh,
ifnull(sum(case when type in (2, 3,5) then b.pump else 0 end), 0)                    as pumpTh,
ifnull(sum(case when type in (2, 3,5) then b.win_lose else 0 end), 0)                as winLoseTh
from
sys_members m
left join sys_bet_info b on b.card = m.card

    AND b.table_id = #{reportSearch.tableId}


    AND b.boot_num = #{reportSearch.bootNum}


    AND b.game_id = #{reportSearch.gameId}


    AND (

        ( b.version >= #{item.minVersion}
        AND b.version <= #{item.maxVersion}
        AND table_id = #{item.tableId}
        )

    )


    AND b.version = -1




            AND m.card = #{reportSearch.card}




        AND (m.card = #{reportSearch.card} or m.parent_card = #{reportSearch.card})


        AND m.is_admin = 0

SELECT
ifnull(sum(case when type in (0, 1,4) then b.win_lose else 0 end), 0)  as winLose,
ifnull(sum(case when type in (0, 1,4) then b.water else 0 end), 0)  as water,

ifnull(sum(case when type in (2, 3,5) then b.win_lose else 0 end), 0)  as winLoseTh,
ifnull(sum(case when type in (2, 3,5) then b.water else 0 end), 0)  as waterTh
FROM
sys_bet_info b


        AND b.card = #{reportSearch.card}

    and (

        ( b.version >= #{item.minVersion}
        AND b.version <= #{item.maxVersion}
        AND b.table_id = #{item.tableId}
        )

    )
