mapper.select:
SELECT r.table_id AS tableId, r.chip AS chip, r.cash AS cash, r.insurance AS insurance, r.chip_add AS chipAdd, r.cash_add AS cashAdd, r.insurance_add AS insuranceAdd, r.water AS water, r.win AS win, r.insurance_win AS insuranceWin, r.create_time AS createTime, r.remark AS remark, r.chip_th AS chipTh, r.cash_th AS cashTh, r.insurance_th AS insuranceTh, r.chip_add_th AS chipAddTh, r.cash_add_th AS cashAddTh, r.insurance_add_th AS insuranceAddTh, r.water_th AS waterTh, r.win_th AS winTh, r.insurance_win_th AS insuranceWinTh FROM sys_receipt r
AND r.table_id = #{tableId}
AND date_format( r.create_time ,'%Y-%m-%d')>= #{startTime}
AND date_format( r.create_time ,'%Y-%m-%d')<= #{endTime}
ORDER BY r.create_time DESC
