mapper.select:
SELECT id,
game_id gameId,
card,
table_id tableId,
boot bootNum,
game gameNum,
`option`,
`type`,
amount,
result,
win,
create_by createBy,
bet_time betTime,
update_time updateTime
FROM
sys_bet_update_record


        AND card = #{card}


        AND table_id = #{tableId}


        AND game_id = #{gameId}


        AND boot = #{bootNum}


        AND game = #{gameNum}


        AND create_by = #{createBy}


        AND update_time >= #{startTime}


        AND update_time <= #{endTime}


ORDER BY update_time desc
