mapper.select:



        AND game_id =#{gameId}

    and is_delete=0

ORDER BY create_time desc

SELECT IFNULL(SUM(chip_point_base),0) as chipPointBase, IFNULL(SUM(cash_point_base),0)as cashPointBase, IFNULL(SUM(insurance_point_base),0)as insurancePointBase, IFNULL(SUM(chip_point_base_th),0) as chipPointBaseTh, IFNULL(SUM(cash_point_base_th),0)as cashPointBaseTh, IFNULL(SUM(insurance_point_base_th),0)as insurancePointBaseTh FROM sys_table_management
AND game_id =#{gameId}
and is_delete=0

SELECT t.table_id as tableId FROM sys_table_management t where t.is_delete=0
AND t.game_id =#{gameId}
ORDER BY t.table_id asc

mapper.delete:
update sys_table_management
set is_delete=1
where id = #{id}

mapper.select:



        and ip= #{ip}


        and table_id= #{tableId}


        AND id !=#{id}


and is_delete=0
